dding\":{\"top\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|50\",\"left\":\"var:preset|spacing|50\",\"right\":\"var:preset|spacing|50\"},\"margin\":{\"top\":\"0\",\"bottom\":\"0\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull\" style=\"margin-top:0;margin-bottom:0;padding-top:var(--wp--preset--spacing--50);padding-right:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50);padding-left:var(--wp--preset--spacing--50)\"><!-- wp:group {\"align\":\"wide\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|50\",\"left\":\"var:preset|spacing|50\",\"right\":\"var:preset|spacing|50\"}},\"border\":{\"radius\":\"16px\"}},\"backgroundColor\":\"base-2\",\"layout\":{\"type\":\"default\"}} -->\n<div class=\"wp-block-group alignwide has-base-2-background-color has-background\" style=\"border-radius:16px;padding-top:var(--wp--preset--spacing--50);padding-right:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50);padding-left:var(--wp--preset--spacing--50)\"><!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">We’ve worked with some of the best companies.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:spacer {\"height\":\"var:preset|spacing|10\"} -->\n<div style=\"height:var(--wp--preset--spacing--10)\" aria-hidden=\"true\" class=\"wp-block-spacer\"></div>\n<!-- /wp:spacer -->\n\n<!-- wp:group {\"align\":\"wide\",\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|20\",\"padding\":{\"right\":\"0\",\"left\":\"0\",\"top\":\"0\",\"bottom\":\"0\"}}},\"layout\":{\"type\":\"flex\",\"flexWrap\":\"wrap\",\"justifyContent\":\"center\"}} -->\n<div class=\"wp-block-group alignwide\" style=\"padding-top:0;padding-right:0;padding-bottom:0;padding-left:0\"><!-- wp:image {\"width\":\"150px\",\"aspectRatio\":\"4/3\",\"scale\":\"contain\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image is-resized\"><img alt=\"\" style=\"aspect-ratio:4/3;object-fit:contain;width:150px\" /></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"width\":\"150px\",\"aspectRatio\":\"4/3\",\"scale\":\"contain\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image is-resized\"><img alt=\"\" style=\"aspect-ratio:4/3;object-fit:contain;width:150px\" /></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"width\":\"150px\",\"aspectRatio\":\"4/3\",\"scale\":\"contain\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image is-resized\"><img alt=\"\" style=\"aspect-ratio:4/3;object-fit:contain;width:150px\" /></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"width\":\"150px\",\"aspectRatio\":\"4/3\",\"scale\":\"contain\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image is-resized\"><img alt=\"\" style=\"aspect-ratio:4/3;object-fit:contain;width:150px\" /></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"width\":\"150px\",\"aspectRatio\":\"4/3\",\"scale\":\"contain\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image is-resized\"><img alt=\"\" style=\"aspect-ratio:4/3;object-fit:contain;width:150px\" /></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\";}i:1;O:8:\"stdClass\":7:{s:2:\"id\";i:422275;s:5:\"title\";O:8:\"stdClass\":1:{s:8:\"rendered\";s:25:\"Three columns of services\";}s:7:\"content\";O:8:\"stdClass\":2:{s:8:\"rendered\";s:2646:\"\n<div class=\"wp-block-group alignfull is-layout-constrained wp-container-core-group-is-layout-10 wp-block-group-is-layout-constrained\" style=\"padding-top:var(--wp--preset--spacing--50);padding-right:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50);padding-left:var(--wp--preset--spacing--50)\">\n<div class=\"wp-block-columns alignwide is-layout-flex wp-container-core-columns-is-layout-1 wp-block-columns-is-layout-flex\">\n<div class=\"wp-block-column is-layout-flow wp-block-column-is-layout-flow\" style=\"flex-basis:33%\">\n<p class=\"has-heading-font-family has-large-font-size\" style=\"line-height:1.2\">We recognize the role architecture plays in shaping a sustainable future.</p>\n</div>\n\n\n\n<div class=\"wp-block-column is-layout-flow wp-container-core-column-is-layout-2 wp-block-column-is-layout-flow\">\n<div class=\"wp-block-group is-nowrap is-layout-flex wp-container-core-group-is-layout-6 wp-block-group-is-layout-flex\">\n<div class=\"wp-block-group is-vertical is-content-justification-left is-nowrap is-layout-flex wp-container-core-group-is-layout-4 wp-block-group-is-layout-flex\">\n<p style=\"font-style:normal;font-weight:500\">Consulting</p>\n\n\n\n<p>Our vision is to be at the forefront of architectural innovation, fostering a global community of architects and enthusiasts united by a passion for creating spaces.</p>\n</div>\n\n\n\n<div class=\"wp-block-group is-vertical is-nowrap is-layout-flex wp-container-core-group-is-layout-5 wp-block-group-is-layout-flex\">\n<p style=\"font-style:normal;font-weight:500\">Project Management</p>\n\n\n\n<p>Our vision is to be at the forefront of architectural innovation, fostering a global community of architects and enthusiasts united by a passion for creating spaces.</p>\n</div>\n</div>\n\n\n\n<div class=\"wp-block-group is-nowrap is-layout-flex wp-container-core-group-is-layout-9 wp-block-group-is-layout-flex\">\n<div class=\"wp-block-group is-vertical is-nowrap is-layout-flex wp-container-core-group-is-layout-7 wp-block-group-is-layout-flex\">\n<p style=\"font-style:normal;font-weight:500\">Design</p>\n\n\n\n<p>Our vision is to be at the forefront of architectural innovation, fostering a global community of architects and enthusiasts united by a passion for creating spaces.</p>\n</div>\n\n\n\n<div class=\"wp-block-group is-vertical is-nowrap is-layout-flex wp-container-core-group-is-layout-8 wp-block-group-is-layout-flex\">\n<p style=\"font-style:normal;font-weight:500\">Maintenance</p>\n\n\n\n<p>Our vision is to be at the forefront of architectural innovation, fostering a global community of architects and enthusiasts united by a passion for creating spaces.</p>\n</div>\n</div>\n</div>\n</div>\n</div>\n\";s:9:\"protected\";b:0;}s:4:\"meta\";O:8:\"stdClass\":8:{s:13:\"wpop_keywords\";s:0:\"\";s:16:\"wpop_description\";s:0:\"\";s:19:\"wpop_viewport_width\";i:800;s:16:\"wpop_block_types\";a:0:{}s:11:\"wpop_locale\";s:5:\"en_US\";s:15:\"wpop_wp_version\";s:0:\"\";s:25:\"wpop_contains_block_types\";s:50:\"core/column,core/columns,core/group,core/paragraph\";s:9:\"footnotes\";s:0:\"\";}s:14:\"category_slugs\";a:2:{i:0;s:8:\"services\";i:1;s:4:\"text\";}s:13:\"keyword_slugs\";a:0:{}s:15:\"pattern_content\";s:3730:\"<!-- wp:group {\"align\":\"full\",\"style\":{\"spacing\":{\"padding\":{\"top\":\"var:preset|spacing|50\",\"right\":\"var:preset|spacing|50\",\"bottom\":\"var:preset|spacing|50\",\"left\":\"var:preset|spacing|50\"}}},\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group alignfull\" style=\"padding-top:var(--wp--preset--spacing--50);padding-right:var(--wp--preset--spacing--50);padding-bottom:var(--wp--preset--spacing--50);padding-left:var(--wp--preset--spacing--50)\"><!-- wp:columns {\"align\":\"wide\",\"style\":{\"spacing\":{\"blockGap\":{\"top\":\"var:preset|spacing|40\",\"left\":\"var:preset|spacing|40\"}}}} -->\n<div class=\"wp-block-columns alignwide\"><!-- wp:column {\"width\":\"33%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:33%\"><!-- wp:paragraph {\"style\":{\"typography\":{\"lineHeight\":\"1.2\"}},\"fontSize\":\"large\",\"fontFamily\":\"heading\"} -->\n<p class=\"has-heading-font-family has-large-font-size\" style=\"line-height:1.2\">We recognize the role architecture plays in shaping a sustainable future.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacing|40\"}}} -->\n<div class=\"wp-block-column\"><!-- wp:group {\"style\":{\"spacing\":{\"blockGap\":\"var:preset|spacin